	var qtdVezes = getValueForm("qtdVezes", "") + "";
	var numProcesso;
	var userId;


	var clientService = fluigAPI.getAuthorizeClientService();
	numProcesso = getValue('WKNumProces');
	userId = getValue("WKUser");

	for (var parcAtual = 1; parcAtual <= qtdVezes; parcAtual++) {
		try {
