					// IMPRIME OS
					// LOGS-------------------------------------------

				}
			}

			params["la_pedido_itens"] = lst_la_pedido_itens;

			params["la_aen_pedido"] = lst_la_aen_pedido;

			params["lr_retirada"] = {};
